import { motion } from "framer-motion";

const SideBar = ({ open }) => {
  const x = useMotionValue(0)
const input = [-200, 0, 200]
const output = [0, 1, 0]
const opacity = useTransform(x, input, output)
  const variants = {
    hidden: { opacity: 0 },
    visible: { opacity: 0.8 },
  };
  return (
    <motion.div
      initial="hidden"
      animate="visible"
      variants={variants}
      className={
        open
          ? " origin-right-left transform  delay-500   fixed right-0 lg:w-2/6  mr-1 w-3/4 bg-gray-100 h-full mt-2 "
          : "hidden  "
      }
    >asas</motion.div>
  );
};

export default SideBar;
