import React from 'react';
import {useSpring, animated} from 'react-spring';

const SideBar = ({ open }) => {
  open=true;
  const props = useSpring({ from: {opacity: 1,marginRight:0}}, {to:{opacity:0,marginRight:500 }},{config:{delay:1000}});
  return (

    <animated.div style={props}
      className={
        open
          ? " fixed right-0 lg:w-2/6  mr-1 w-3/4 bg-gray-100 h-full mt-2 "
          : "hidden  "
      }
    ></animated.div>
  );
};


export default SideBar;
