import React from 'react';
import {Spring} from 'react-spring';


const SideBar = ({ open }) => {
  return (
    <Spring>
      from={{opacity:0}}
      to={{opacity:1}}
       {props =>{
         <div style={props}>
  className={
        open
          ? " transform origin-right-left transition delay-700  duration-300 ease-in-out  fixed right-0 lg:w-2/6  mr-1 w-3/4 bg-gray-100 h-full mt-2 "
          : "hidden  "
      }
    >
         </div>
       }}
    </Spring>

    
    
  );
};


export default SideBar;
