import { motion, useMotionValue,useTransform } from "framer-motion";

const SideBar = ({ open }) => {
  const x = useMotionValue(10)
  const y = useTransform(x, value => value * 2)

  
  return (
    <motion.div
     animate={{ x: 100 }}
      className={
        open
          ? " origin-right-left transform  delay-500   fixed right-0 lg:w-2/6  mr-1 w-3/4 bg-gray-100 h-full mt-2 "
          : "hidden  "
      }
    >asas</motion.div>
  );
};

export default SideBar;
