import React from 'react';
import {useSpring, animated} from 'react-spring';

const SideBar = ({ open }) => {
  open=true;
  const AnimatedView = animated(<div  className={
    open
      ? " fixed right-0 lg:w-2/6  mr-1 w-3/4 bg-gray-100 h-full mt-2 "
      : "hidden  "
  }
  ></div>)
  const props = useSpring({ from: {opacity: 1}}, {to:{opacity:0 }});
  return (

    AnimatedView
     
   
  );
};


export default SideBar;
