    <div className="flex flex-row border-t border-gray-400 pt-2">
      <div className="w-24 mx-auto flex flex-row border rounded-full border-gray-400  justify-between">
        <p className="px-2 text-gray-700 font-bold hover: cursor-pointer">-</p>
        <span>0</span>
        <p className="px-2 text-gray-700 font-bold hover: cursor-pointer" onClick={Handle}>+</p>
      </div>
      <div className="mx-auto">
        <span>Add</span>
      </div>
    </div>
  );
}
