import React from 'react';
im
import Header from "../templates/partials/header";
import Footer from "../templates/partials/footer";
import ProductCard from "../components/products/productCard";
import { ProductsInCartProvider } from "../context/productInCartContext";
import firebase from 'firebase/app';
import "firebase/firestore";
import { useState } from "react";

export default function Home(props) {

  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    const unsubcribe = firebase
      .firestore()
      .collection("products")
      .onSnapshot(snapshot => {
        const newProducts = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setProducts(newProducts);
      });
      setLoading(false);
    return () => unsubcribe();
  }, []);


  return (
    <div>
      <ProductsInCartProvider>
        <Header />
        <main className="  bg-red-700  background  ">
          <div className=" flex flex w-full ">
            <div className="pt-20 flex  flex-row mx-auto w-5/6 flex-wrap">
              <ProductCard product={props.product} />
            </div>
          </div>
        </main>
      </ProductsInCartProvider>

      <Footer />

      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
}

// This function gets called at build time on server-side.
// It won't be called on client-side, so you can even do
// direct database queries. See the "Technical details" section.
export async function getStaticProps() {
  const product = {
    name: "Pizza de Jamon",
    pictureUrl: "pizza-mixta.jpg",
    ingredients: "garlic-chesse-bacon",
    price: "5.90",
  };

  return {
    props: {
      product,
    },
  };
}
