import React from 'react';
import {useSpring, animated} from 'react-spring';

const SideBar = ({ open }) => {
  const props = useSpring({opacity: 1, from: {opacity: 0}});
  return (

    <div
      className={
        open
          ? " transform origin-right-left transition delay-700  duration-300 ease-in-out  fixed right-0 lg:w-2/6  mr-1 w-3/4 bg-gray-100 h-full mt-2 "
          : "hidden  "
      }
    ></div>
  );
};


export default SideBar;
